import React from 'react';
import { MapContainer, TileLayer, GeoJSON, } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icons in React Leaflet
// @ts-ignore
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Define the props that this component will receive from App.tsx
interface MapComponentProps {
  regions: any[]; // The GeoJSON feature data
  getRegionStyle: (feature: any) => object;
  onEachFeature: (feature: any, layer: L.Layer) => void;
}

const MapComponent: React.FC<MapComponentProps> = ({ regions, getRegionStyle, onEachFeature }) => {
  return (
    <MapContainer 
      center={[-22, 17]} 
      zoom={6} 
      style={{ height: '100%', width: '100%' }}
    >
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />

      {/* Render a SINGLE GeoJSON component with all the features. This is more efficient. */}
      <GeoJSON
        data={regions as any}
        style={getRegionStyle}
        onEachFeature={onEachFeature}
      />
    </MapContainer>
  );
};

export default MapComponent;
