import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, GeoJSON, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';

// Fix for default marker icons in React Leaflet
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Types
type Region = {
  id: number;
  name: string;
  fiber_status: 'Active' | 'Planned' | 'Unavailable';
  properties: {
    population?: number;
    area_km2?: number;
  };
};

type GeoJSONFeature = {
  type: 'Feature';
  properties: Region;
  geometry: {
    type: string;
    coordinates: number[][][] | number[][][][];
  };
};

const App: React.FC = () => {
  const [regions, setRegions] = useState<GeoJSONFeature[]>([]);
  const [selectedRegion, setSelectedRegion] = useState<Region | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [mapReady, setMapReady] = useState(false);

  // Default center and zoom for Namibia
  const center: [number, number] = [-22, 17];
  const zoom = 6;

  // Fix for map not displaying properly
  useEffect(() => {
    setMapReady(true);
  }, []);

  // Fetch regions data from the API
  useEffect(() => {
    const fetchRegions = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/v1/regions/geojson');
        setRegions(response.data.features);
      } catch (err) {
        console.error('Error fetching regions:', err);
        setError('Failed to load region data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchRegions();
  }, []);

  // Style for GeoJSON features based on fiber status
  const getRegionStyle = (feature: any) => {
    if (!feature || !feature.properties) return {};

    const status = feature.properties.fiber_status || 'Unavailable';
    let fillColor = '#e0e0e0'; // Default color (light gray)

    switch (status) {
      case 'Active':
        fillColor = '#4CAF50'; // Green
        break;
      case 'Planned':
        fillColor = '#FFC107'; // Yellow
        break;
      case 'Unavailable':
        fillColor = '#F44336'; // Red
        break;
      default:
        break;
    }

    return {
      fillColor,
      weight: 1,
      opacity: 1,
      color: 'white',
      fillOpacity: 0.7,
    };
  };

  // Handle click on a region
  const handleRegionClick = (feature: any) => {
    setSelectedRegion(feature.properties);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl font-semibold">Loading map data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl font-semibold text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="App">
      <header className="app-header">
        <h1>Namibia Fiber Coverage Map</h1>
        <p className="subtitle">Visualizing fiber optic coverage across regions</p>
      </header>
      <main>
        <div style={{ height: 'calc(100vh - 200px)', width: '100%', position: 'relative' }}>
          {!mapReady ? (
            <div className="flex items-center justify-center h-full">
              <div className="text-xl font-semibold">Initializing map...</div>
            </div>
          ) : (
            <MapContainer 
              center={center} 
              zoom={zoom} 
              style={{ height: '100%', width: '100%' }}
              zoomControl={true}
            >
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              />
              {regions.map((region, index) => (
                <GeoJSON
                  key={index}
                  data={region as any}
                  style={getRegionStyle(region)}
                  eventHandlers={{
                    click: () => handleRegionClick(region)
                  }}
                >
                  <Popup>
                    <div>
                      <h3 className="font-bold">{region.properties.name}</h3>
                      <p>Status: {region.properties.fiber_status}</p>
                    </div>
                  </Popup>
                </GeoJSON>
              ))}
            </MapContainer>
          )}
        </div>
        {/* Sidebar */}
        <div className="w-80 bg-white border-r border-gray-200 p-4 overflow-y-auto">
          <h2 className="text-lg font-semibold mb-4">Region Information</h2>

          {selectedRegion ? (
            <div className="bg-gray-50 p-4 rounded-lg">
              <h3 className="text-xl font-bold mb-2">{selectedRegion.name}</h3>

              <div className="mb-3">
                <span className="inline-block px-2 py-1 text-sm font-medium rounded-full mb-2"
                  style={{
                    backgroundColor: 
                      selectedRegion.fiber_status === 'Active' ? '#4CAF50' :
                      selectedRegion.fiber_status === 'Planned' ? '#FFC107' : '#F44336',
                    color: 'white'
                  }}>
                  {selectedRegion.fiber_status}
                </span>
              </div>

              <div className="space-y-2">
                {selectedRegion.properties.population && (
                  <p><span className="font-medium">Population:</span> {selectedRegion.properties.population.toLocaleString()}</p>
                )}
                {selectedRegion.properties.area_km2 && (
                  <p><span className="font-medium">Area:</span> {selectedRegion.properties.area_km2.toLocaleString()} kmÂ²</p>
                )}
              </div>
            </div>
          ) : (
            <div className="text-gray-500 italic">
              Click on a region to see details
            </div>
          )}

          <div className="mt-6">
            <h3 className="font-semibold mb-2">Legend</h3>
            <div className="space-y-2">
              <div className="flex items-center">
                <div className="w-4 h-4 bg-green-500 mr-2"></div>
                <span>Active Coverage</span>
              </div>
              <div className="flex items-center">
                <div className="w-4 h-4 bg-yellow-400 mr-2"></div>
                <span>Planned Coverage</span>
              </div>
              <div className="flex items-center">
                <div className="w-4 h-4 bg-red-500 mr-2"></div>
                <span>No Coverage</span>
              </div>
            </div>
          </div>
        </div>
      </main>
      <footer className="app-footer">
        <p> Namibia Fiber Coverage Mapper {new Date().getFullYear()}</p>
      </footer>
    </div>
  );
};

export default App;
